namespace Utility

module IO =
    open System
    open System.IO

    let readAllLinesAsync filename =
        async {
            let! content = File.ReadAllLinesAsync(filename) |> Async.AwaitTask
            return content
        }

    let readAllTextAsync filename =
        async {
            let! content = File.ReadAllTextAsync(filename) |> Async.AwaitTask
            return content
        }

    let clearScreen () =
        if System.Environment.OSVersion.Platform = PlatformID.Win32NT then
            System.Console.Clear()
        else
            System.Console.Write("\x1B[2J\x1B[H") // Codice di escape ANSI per Unix-like systems


    let banner year day =
        clearScreen ()
        // let encode_banner =  //     | 2022 -> ""
        //     | _ -> ""
        //     | 2022 -> ""
        //     | _ -> ""
        let encode_banner =
            match year with
            | 2022 ->
                ""
            | _ ->
                ""

        Convert.FromBase64String(encode_banner)
        |> System.Text.Encoding.UTF8.GetString
        |> printf "%s\n\n"

        printf "\t D A Y   %d\n\n" day
